var mysql = require('mysql');
var queries = require('./queries');
var config = require('./config');

/************************************
*                                   *
* CONFIGURACION DE LA BASE DE DATOS *
*                                   *
*************************************/







//----------------------------------------------------------
// 	DEFINICION DEL MODULO
//

var DB = function( config ){

    config = config || {};

}

DB.prototype.getSingleData = function(data,callback){
   var Query = queries.SQLGETSINGLEDATA;
  
   DB.prototype.runQuery(Query,function(res){
      callback(res);
   });
}


DB.prototype.runQuery = function(Query, Callback){
    
    var connection = mysql.createConnection(config.dbConfig);
    connection.connect(function(error){
        if(error) throw error;   
    });    
    var sql = connection.query(Query, function(error, res){
        if(error) throw error;
        if(Callback){    
              
           Callback(res);
        }
    });
    console.log(sql.sql);
    // end conection
    connection.end();
}




module.exports = DB;
