var editor = function(io, socket){

    //Mensaje enviado por el cliente 
    socket.on('message_client',function(msj){
        
        console.log("messaje on client");

    });

    // mensaje de cambio en el editor
    socket.on('change_client', function(reqcode){
        console.log(reqcode);
        io.emit('change_editor',{code:reqcode.code});

    });
}

exports.editor = editor;